      {
        "type": "Theorem",
        "label": "thm:monoidHom_eq_of_isCyclic",
        "header": "Theorem",
        "hypothesis": [
          {
            "type": "let_statement",
            "variable_name": "G",
            "variable_type": "Type* with a group structure",
            "statement": "Let G be a group."
          },
          {
            "type": "let_statement",
            "variable_name": "G'",
            "variable_type": "Type* with a group structure",
            "statement": "Let G' be a group."
          },
          {
            "type": "let_statement",
            "variable_name": "a",
            "variable_type": "element of G",
            "statement": "Let a ∈ G be a generator of the cyclic group G."
          },
          {
            "type": "assume_statement",
            "assumption": "For every g ∈ G there exists an integer n such that g = a^n; that is, G is cyclic with generator a."
          },
          {
            "type": "let_statement",
            "variable_name": "φ",
            "variable_type": "group isomorphism from G to G'",
            "statement": "Let φ : G → G' be an isomorphism."
          },
          {
            "type": "let_statement",
            "variable_name": "ψ",
            "variable_type": "group isomorphism from G to G'",
            "statement": "Let ψ : G → G' be an isomorphism."
          },
          {
            "type": "assume_statement",
            "assumption": "φ(a) = ψ(a)."
          }
        ],
        "claim": "For every x ∈ G, φ(x) is completely determined by φ(a). More precisely, if φ, ψ : G → G' are isomorphisms with φ(a) = ψ(a), then φ(x) = ψ(x) for all x ∈ G.",
        "proof": [
          {
            "type": "assume_statement",
            "assumption": "Let g ∈ G be arbitrary."
          },
          {
            "type": "some_statement",
            "variable_name": "n",
            "variable_kind": "integer",
            "properties": "g = a^n",
            "statement": "By the cyclicity hypothesis, there exists an integer n such that g = a^n."
          },
          {
            "type": "assert_statement",
            "claim": "For any integer n and any x ∈ G, one has φ(x^n) = φ(x)^n and ψ(x^n) = ψ(x)^n.",
            "proof_method": "Because φ and ψ are group homomorphisms, they respect integer powers."
          },
          {
            "type": "assert_statement",
            "claim": "φ(g) = φ(a)^n and ψ(g) = ψ(a)^n.",
            "proof_method": "Apply the homomorphism property for powers to x = a and the chosen integer n, using g = a^n."
          },
          {
            "type": "assert_statement",
            "claim": "φ(a)^n = ψ(a)^n.",
            "proof_method": "Substitute φ(a) = ψ(a) into the expressions for the n-th powers."
          },
          {
            "type": "assert_statement",
            "claim": "φ(g) = ψ(g).",
            "proof_method": "Combine the equalities φ(g) = φ(a)^n, φ(a)^n = ψ(a)^n, and ψ(a)^n = ψ(g)."
          },
          {
            "type": "conclude_statement",
            "claim": "For all g ∈ G, φ(g) = ψ(g)."
          }
        ]
      }
    ]
  }
}

